import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FaFileAlt, FaSearch } from 'react-icons/fa';
import '../styles/Home.css';

// Chart.js
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement
} from 'chart.js';
import { Bar, Pie } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Home = () => {
  const [forms, setForms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('all');
  const [dashboardData, setDashboardData] = useState([]);
  const [formStats, setFormStats] = useState({
    total: 0,
    pending: 0,
    approved: 0,
    rejected: 0
  });

  useEffect(() => {
    fetchForms();
  }, []);

  const fetchForms = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('http://192.168.17.15:5000/api/forms', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setForms(response.data);

      // Calculate form statistics
      const stats = response.data.reduce((acc, form) => {
        acc.total++;
        switch(form.status?.toLowerCase()) {
          case 'pending': acc.pending++; break;
          case 'approved': acc.approved++; break;
          case 'rejected': acc.rejected++; break;
        }
        return acc;
      }, { total: 0, pending: 0, approved: 0, rejected: 0 });
      setFormStats(stats);

      // Process Purchase Request data for charts
      const currentYear = new Date().getFullYear();
      const purchaseRequests = response.data.filter(f => f.form_name === 'Purchase Request');

      const deptMap = {};
      purchaseRequests.forEach((form) => {
        let details = {};
        try {
          details = JSON.parse(form.details);
        } catch (err) {}
        const formYear = details.date ? new Date(details.date).getFullYear() : null;
        if (formYear === currentYear) {
          const dept = form.department || 'Unknown Department';
          const grand = parseFloat(details.grandTotal) || 0;
          if (!deptMap[dept]) {
            deptMap[dept] = { total: 0, count: 0 };
          }
          deptMap[dept].total += grand;
          deptMap[dept].count += 1;
        }
      });

      const dashArray = Object.keys(deptMap).map(d => ({
        department: d,
        total: deptMap[d].total,
        count: deptMap[d].count
      }));
      setDashboardData(dashArray);

    } catch (err) {
      console.error('Error fetching forms:', err);
      setError('Failed to load forms. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusClass = (status) => {
    switch (status?.toLowerCase()) {
      case 'pending': return 'status-pending';
      case 'approved': return 'status-approved';
      case 'rejected': return 'status-rejected';
      default: return 'status-draft';
    }
  };

  const filteredForms = forms.filter(form => {
    const matchesSearch = form.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         form.requestor?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = selectedStatus === 'all' || form.status?.toLowerCase() === selectedStatus.toLowerCase();
    return matchesSearch && matchesStatus;
  });

  if (loading) return <div className="loading">Loading dashboard data...</div>;
  if (error) return <div className="error">{error}</div>;

  // Chart data preparation
  const labels = dashboardData.map(d => d.department);
  const totals = dashboardData.map(d => d.total);
  const colors = [
    '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0',
    '#9966FF', '#FF9F40', '#F690B8', '#66DDAA',
    '#C19A6B', '#87CEEB', '#CD5C5C', '#7FFFD4'
  ];
  const backgroundColors = labels.map((_, i) => colors[i % colors.length]);

  const barData = {
    labels,
    datasets: [{
      label: 'Total Amount',
      data: totals,
      backgroundColor: backgroundColors
    }]
  };

  const barOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: false },
      title: {
        display: true,
        text: 'Department-wise Purchase Request Amounts'
      }
    },
    scales: {
      y: {
        ticks: {
          callback: value => Number(value).toLocaleString('en-US')
        }
      }
    }
  };

  const pieData = {
    labels,
    datasets: [{
      label: 'Total Amount',
      data: totals,
      backgroundColor: backgroundColors
    }]
  };

  return (
    <div className="home-container">
      <div className="dashboard-header">
        <h1>Forms System Dashboard</h1>
        <div className="stats-cards">
          <div className="stat-card total">
            <h3>Total Forms</h3>
            <p>{formStats.total}</p>
          </div>
          <div className="stat-card pending">
            <h3>Pending</h3>
            <p>{formStats.pending}</p>
          </div>
          <div className="stat-card approved">
            <h3>Approved</h3>
            <p>{formStats.approved}</p>
          </div>
          <div className="stat-card rejected">
            <h3>Rejected</h3>
            <p>{formStats.rejected}</p>
          </div>
        </div>
      </div>

      <div className="dashboard-section">
        <h2>Purchase Requests Overview (Current Year)</h2>
        {dashboardData.length === 0 ? (
          <p>No purchase request data available for the current year</p>
        ) : (
          <div className="dashboard-grid">
            <div className="dashboard-card summary-table">
              <h3>Department Summary</h3>
              <table className="dashboard-table">
                <thead>
                  <tr>
                    <th>Department</th>
                    <th>Count</th>
                    <th>Total Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {dashboardData.map((row) => (
                    <tr key={row.department}>
                      <td>{row.department}</td>
                      <td>{row.count}</td>
                      <td>{row.total.toLocaleString('en-US', { minimumFractionDigits: 2 })}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div className="dashboard-card chart">
              <h3>Amount Distribution</h3>
              <div className="chart-container">
                <Bar data={barData} options={barOptions} />
              </div>
            </div>

            <div className="dashboard-card chart">
              <h3>Department Share</h3>
              <div className="chart-container">
                <Pie data={pieData} />
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="recent-forms-section">
        <div className="section-header">
          <h2>Recent Forms</h2>
          <div className="filters">
            <div className="search-box">
              <FaSearch className="search-icon" />
              <input
                type="text"
                placeholder="Search forms..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <select
              value={selectedStatus}
              onChange={(e) => setSelectedStatus(e.target.value)}
              className="status-filter"
            >
              <option value="all">All Status</option>
              <option value="pending">Pending</option>
              <option value="approved">Approved</option>
              <option value="rejected">Rejected</option>
            </select>
          </div>
        </div>

        <div className="forms-table-container">
          <table className="forms-table">
            <thead>
              <tr>
                <th>Form ID</th>
                <th>Form Type</th>
                <th>Requestor</th>
                <th>Department</th>
                <th>Status</th>
                <th>Created Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {filteredForms.slice(0, 10).map(form => (
                <tr key={form.id}>
                  <td>{form.id}</td>
                  <td>{form.form_name}</td>
                  <td>{form.user_name}</td>
                  <td>{form.department}</td>
                  <td>
                    <span className={`status ${getStatusClass(form.status)}`}>
                      {form.status || 'Draft'}
                    </span>
                  </td>
                  <td>{new Date(form.createdAt).toLocaleDateString()}</td>
                  <td>
                    <Link to={`/view-form/${form.id}`} className="view-link">
                      View Details
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Home;
