import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const MyForms = () => {
  const [forms, setForms] = useState([]);
  const [loading, setLoading] = useState(true);
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    fetchMyForms();
  }, []);

  const fetchMyForms = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://192.168.17.15:5000/api/forms/my-forms', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setForms(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching forms:', error);
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      const form = forms.find(f => f.id === id);
      if (form.status !== 'Draft') {
        alert('ไม่สามารถลบฟอร์มที่อยู่ในสถานะ Waiting For Approve หรือ Approved ได้');
        return;
      }

      const token = localStorage.getItem('token');
      await axios.delete(`http://192.168.17.15:5000/api/forms/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      setForms(forms.filter(form => form.id !== id));
      alert('ลบฟอร์มสำเร็จ');
    } catch (error) {
      console.error('Error deleting form:', error);
      alert('เกิดข้อผิดพลาดในการลบฟอร์ม');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Draft':
        return '#FFA500';
      case 'Waiting For Approve':
        return '#3498db';
      case 'Approved':
        return '#2ecc71';
      default:
        return '#000000';
    }
  };

  if (loading) {
    return <div>กำลังโหลด...</div>;
  }

  return (
    <div className="container">
      <h2>My Forms - {user?.name}</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Form Name</th>
              <th>Department</th>
              <th>Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {forms.map((form) => (
              <tr key={form.id}>
                <td>{form.form_name}</td>
                <td>{form.department}</td>
                <td>{new Date(form.request_date).toLocaleDateString()}</td>
                <td>
                  <span style={{ 
                    color: getStatusColor(form.status),
                    fontWeight: 'bold'
                  }}>
                    {form.status}
                  </span>
                </td>
                <td>
                  <Link to={`/view-form/${form.id}`} className="view-btn">
                    View
                  </Link>
                  {form.status === 'Draft' && (
                    <button
                      onClick={() => handleDelete(form.id)}
                      className="delete-btn"
                    >
                      Delete
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MyForms;
