import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import PurchaseRequestForm from './pages/PurchaseRequestForm';
import CapexRequestForm from './pages/CapexRequestForm';
import TravelRequestForm from './pages/TravelRequestForm';
import Home from './pages/Home';
import NewForm from './pages/NewForm';    
import ViewForm from './pages/ViewForm';
import Login from './pages/login';
import Register from './pages/register';
import ForgotPassword from './pages/forgot-password';
import ResetPassword from './pages/reset-password';
import './App.css';

// ProtectedRoute component
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  if (!token) {
    return <Navigate to="/login" />;
  }
  return children;
};

function App() {
  const token = localStorage.getItem('token');

  return (
    <Router>
      {token && <Header />}
      <div className="app-container" style={{ marginTop: '50px' }}>
        {token && <Sidebar />}
        <div className="content-container">
          <Routes>
            {/* Public routes */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/reset-password" element={<ResetPassword />} />

            {/* Protected routes */}
            <Route path="/" element={
              <ProtectedRoute>
                <Home />
              </ProtectedRoute>
            } />
            <Route path="/new" element={
              <ProtectedRoute>
                <NewForm />
              </ProtectedRoute>
            } />
            <Route path="/view/:id" element={
              <ProtectedRoute>
                <ViewForm />
              </ProtectedRoute>
            } />
            <Route path="/form/purchase-request" element={
              <ProtectedRoute>
                <PurchaseRequestForm />
              </ProtectedRoute>
            } />
            <Route path="/form/capex-request" element={
              <ProtectedRoute>
                <CapexRequestForm />
              </ProtectedRoute>
            } />
            <Route path="/form/travel-request" element={
              <ProtectedRoute>
                <TravelRequestForm />
              </ProtectedRoute>
            } />
            <Route path="/home" element={
              <ProtectedRoute>
                <Home />
              </ProtectedRoute>
            } />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
