      setForms(forms.filter(form => form.id !== id));
      alert('‡∏•‡∏ö‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    } catch (error) {
      console.error('Error deleting form:', error);
      alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏ü‡∏≠‡∏£‡πå‡∏°');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Draft':
        return '#FFA500';
      case 'Waiting For Approve':
        return '#3498db';
      case 'Approved':
        return '#2ecc71';
