              <th>Department</th>
              <th>Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {forms.map((form) => (
              <tr key={form.id}>
                <td>{form.form_name}</td>
                <td>{form.department}</td>
                <td>{new Date(form.request_date).toLocaleDateString()}</td>
                <td>
                  <span className="status-badge" style={{ 
