import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaWindows, FaUser, FaLock, FaExclamationCircle } from 'react-icons/fa';
import '../styles/Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('http://192.168.17.15:5000/api/auth/login', {
        username: credentials.username,
        password: credentials.password
      });

      if (response.data.token) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        navigate('/');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to authenticate. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <img
          src="https://img2.pic.in.th/pic/logo14821dedd19c2ad18.png"
          alt="Newlyweds Foods Logo"
          className="company-logo"
        />
        <h1>Organization Forms System</h1>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <div className="input-with-icon">
              <FaUser className="input-icon" />
              <input
                type="text"
                id="username"
                name="username"
                value={credentials.username}
                onChange={handleChange}
                placeholder="Username"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <div className="input-with-icon">
              <FaLock className="input-icon" />
              <input
                type="password"
                id="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                placeholder="Password"
                required
              />
            </div>
          </div>

          {error && (
            <div className="error-message">
              <FaExclamationCircle className="error-icon" />
              {error}
            </div>
          )}

          <button 
            type="submit" 
            className="windows-login-btn"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="loading-spinner">⌛</span>
                Signing in...
              </>
            ) : (
              <>
                <FaWindows className="windows-icon" />
                Sign in with Domain Account
              </>
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
