import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaWindows } from 'react-icons/fa';
import '../styles/Login.css';

const Login = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
          rememberMe: formData.rememberMe,
          useDomain: true
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'การเข้าสู่ระบบล้มเหลว');
      }

      // เก็บ token ไว้ใน localStorage
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));
      
      // Redirect ไปหน้า home
      window.location.href = '/home';
      
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form-container">
        <div className="login-header">
          <h1>NWFTH Forms System</h1>
          <p>กรุณาเข้าสู่ระบบเพื่อดำเนินการต่อ</p>
        </div>

        <form className="login-form" onSubmit={(e) => handleSubmit(e, false)}>
          <div className="form-group">
            <label htmlFor="email">อีเมล</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="กรอกอีเมลของคุณ"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">รหัสผ่าน</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="กรอกรหัสผ่านของคุณ"
            />
          </div>

          <div className="login-options">
            <label className="remember-me">
              <input
                type="checkbox"
                name="rememberMe"
                checked={formData.rememberMe}
                onChange={handleChange}
              />
              จดจำฉัน
            </label>
            <a href="/forgot-password" className="forgot-password">ลืมรหัสผ่าน?</a>
          </div>

          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'กำลังเข้าสู่ระบบ...' : 'เข้าสู่ระบบ'}
          </button>

          <div className="login-divider">
            <span>หรือ</span>
          </div>

          <button
            type="button"
            className="domain-login-button"
            onClick={(e) => handleSubmit(e, true)}
            disabled={isLoading}
          >
            <FaWindows />
            เข้าสู่ระบบด้วย Domain Account
          </button>

          {error && <div className="error-message">{error}</div>}

          <div className="register-link">
            ยังไม่มีบัญชี? <a href="/register">ลงทะเบียนที่นี่</a>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
